import './App.css';
const changeText=(event)=>{
  console.log(event.target)
  event.target.innerText=event.innerText+"被點了"
}
const Button=(num)=>{
  var output=[];
  for(let i=1;i<num+1;++i)
    output.push(<button onClick={changeText}>第{i}個鍵</button>)
  return output;
}
function App() {
  return (
    <div className="App">
      { Button(10) }

    </div>
  );
}

export default App;
